--INSERT INTO movies VALUES(265,'tt0082661','Lili Marleen','Lili Marleen is the story of a German singer named Wilkie who while working in Switzerland falls in love with a Jewish composer named Robert who’s family is helping people to flee from the Nazis. Roberts family is skeptical of Walkie thinking she could be a Nazi as she becomes famous for singing Lili Marleen.','[{"name": "Roxy Film", "id": 101}, {"name": "Bayerischer Rundfunk (BR)", "id": 7333}, {"name": "Rialto Films", "id": 8658}, {"name": "CIP Filmproduktion GmbH", "id": 14309}]','1981-01-14',0,0,119.99999999999999644,NULL,'[{"id": 10752, "name": "War"}, {"id": 18, "name": "Drama"}, {"id": 10749, "name": "Romance"}]','Released');
