--INSERT INTO movies VALUES(267,'tt0118819','Live Flesh','A scorned ex-convict forces himself into the lives of the couple who put him behind bars.','[{"name": "El Deseo", "id": 49}, {"name": "France 3 Cinéma", "id": 591}, {"name": "CiBy 2000", "id": 7832}]','1997-10-28',0,1535558,103.00000000000000266,NULL,'[{"id": 18, "name": "Drama"}, {"id": 10749, "name": "Romance"}, {"id": 53, "name": "Thriller"}]','Released');
