INSERT INTO movies VALUES(2, 'tt0094675','Ariel','Taisto Kasurinen is a Finnish coal miner whose father has just committed suicide and who is framed for a crime he did not commit. In jail, he starts to dream about leaving the country and starting a new life. He escapes from prison but things don''t go as planned...','[{"name": "Villealfa Filmproduction Oy", "id": 2303}, {"name": "Finnish Film Foundation", "id": 2396}]','1988-10-21',0,0,69.000000000000003552,'English','[{"id": 18, "name": "Drama"}, {"id": 80, "name": "Crime"}]','Released');
INSERT INTO movies VALUES(3,'tt0092149','Shadows in Paradise','An episode in the life of Nikander, a garbage man, involving the death of a co-worker, an affair and much more.','[{"name": "Villealfa Filmproduction Oy", "id": 2303}]','1986-10-16',0,0,75.999999999999996447,NULL,'[{"id": 18, "name": "Drama"}, {"id": 35, "name": "Comedy"}]','Released');
INSERT INTO movies VALUES(5,'tt0113101','Four Rooms','It''s Ted the Bellhop''s first night on the job...and the hotel''s very unusual guests are about to place him in some outrageous predicaments. It seems that this evening''s room service is serving up one unbelievable happening after another.','[{"name": "Miramax Films", "id": 14}, {"name": "A Band Apart", "id": 59}]','1995-12-09',4000000,4300000,98.000000000000007105,NULL,'[{"id": 80, "name": "Crime"}, {"id": 35, "name": "Comedy"}]','Released');
INSERT INTO movies VALUES(6,'tt0107286','Judgment Night','While racing to a boxing match, Frank, Mike, John and Rey get more than they bargained for. A wrong turn lands them directly in the path of Fallon, a vicious, wise-cracking drug lord. After accidentally witnessing Fallon murder a disloyal henchman, the four become his unwilling prey in a savage game of cat &amp; mouse as they are mercilessly stalked through the urban jungle in this taut suspense drama','[{"name": "Universal Pictures", "id": 33}, {"name": "Largo Entertainment", "id": 1644}, {"name": "JVC Entertainment Networks", "id": 4248}]','1993-10-15',0,12136938,110.00000000000000888,NULL,'[{"id": 28, "name": "Action"}, {"id": 53, "name": "Thriller"}, {"id": 80, "name": "Crime"}]','Released');
