INSERT INTO movies VALUES(16,'tt0168629','Dancer in the Dark','Selma, a Czech immigrant on the verge of blindness, struggles to make ends meet for herself and her son, who has inherited the same genetic disorder and will suffer the same fate without an expensive operation. When life gets too difficult, Selma learns to cope through her love of musicals, escaping life''s troubles - even if just for a moment - by dreaming up little numbers to the rhythmic beats of her surroundings.','[{"name": "Fine Line Features", "id": 8}, {"name": "Zentropa Entertainments", "id": 76}, {"name": "Danmarks Radio (DR)", "id": 119}, {"name": "SVT Drama", "id": 157}, {"name": "Arte", "id": 201}, {"name": "Memfis Film", "id": 321}, {"name": "France 3 Cinéma", "id": 591}, {"name": "Angel films", "id": 2996}, {"name": "TV 1000", "id": 4524}, {"name": "Canal+", "id": 5358}, {"name": "Constantin Film Produktion", "id": 5755}, {"name": "Yleisradio (YLE)", "id": 5975}, {"name": "Arte France Cinéma", "id": 6916}, {"name": "Westdeutscher Rundfunk (WDR)", "id": 7025}, {"name": "Blind Spot Pictures Oy", "id": 7330}, {"name": "Vrijzinnig Protestantse Radio Omroep (VPRO)", "id": 8659}, {"name": "Film4", "id": 9349}, {"name": "Liberator Productions", "id": 11239}, {"name": "Icelandic Film", "id": 14937}, {"name": "Film i Väst", "id": 17513}, {"name": "Pain Unlimited GmbH Filmproduktion", "id": 30268}, {"name": "Trust Film Svenska", "id": 53667}, {"name": "Cinematograph A/S", "id": 53668}, {"name": "What Else? B.V", "id": 53669}, {"name": "Filmek A/S", "id": 53670}, {"name": "Lantia Cinema & Audiovisivi", "id": 53671}]','2000-05-17',12800000,40031879,139.99999999999999111,NULL,'[{"id": 18, "name": "Drama"}, {"id": 80, "name": "Crime"}, {"id": 10402, "name": "Music"}]','Released');
