INSERT INTO movies VALUES(33,'tt0105695','Unforgiven','William Munny is a retired, once-ruthless killer turned gentle widower and hog farmer. To help support his two motherless children, he accepts one last bounty-hunter mission to find the men who brutalized a prostitute. Joined by his former partner and a cocky greenhorn, he takes on a corrupt sheriff.','[{"name": "Malpaso Productions", "id": 171}, {"name": "Warner Bros.", "id": 6194}]','1992-08-07',14000000,159157447,131.00000000000000532,NULL,'[{"id": 37, "name": "Western"}]','Released');
