INSERT INTO movies VALUES(63,'tt0114746','Twelve Monkeys','In the year 2035, convict James Cole reluctantly volunteers to be sent back in time to discover the origin of a deadly virus that wiped out nearly all of the earth''s population and forced the survivors into underground communities. But when Cole is mistakenly sent to 1990 instead of 1996, he''s arrested and locked up in a mental hospital. There he meets psychiatrist Dr. Kathryn Railly, and patient Jeffrey Goines, the son of a famous virus expert, who may hold the key to the mysterious rogue group, the Army of the 12 Monkeys, thought to be responsible for unleashing the killer disease.','[{"name": "Universal Pictures", "id": 33}, {"name": "Atlas Entertainment", "id": 507}, {"name": "Classico", "id": 17031}]','1995-12-29',29500000,168840000,129.00000000000000355,NULL,'[{"id": 878, "name": "Science Fiction"}, {"id": 53, "name": "Thriller"}, {"id": 9648, "name": "Mystery"}]','Released');
