INSERT INTO movies VALUES(124,'tt0086961','No End','Taking place in a 1982 Poland a translator loses her husband and becomes a victim of her own sorrow. She looks to sex, to her son, to law, and to hypnotism when she has nothing else in this time of martial law when Solidarity was banned.','[{"name": "P.P. Film Polski", "id": 1587}]','1985-06-17',0,0,109.0000000000000071,NULL,'[{"id": 18, "name": "Drama"}, {"id": 14, "name": "Fantasy"}]','Released');
