--INSERT INTO movies VALUES(11,'tt0076759','Star Wars','Princess Leia is captured and held hostage by the evil Imperial forces in their effort to take over the galactic Empire. Venturesome Luke Skywalker and dashing captain Han Solo team together with the loveable robot duo R2-D2 and C-3PO to rescue the beautiful princess and restore peace and justice in the Empire.','[{"name": "Lucasfilm", "id": 1}, {"name": "Twentieth Century Fox Film Corporation", "id": 306}]','1977-05-25',11000000,775398007,120.99999999999999644,NULL,'[{"id": 12, "name": "Adventure"}, {"id": 28, "name": "Action"}, {"id": 878, "name": "Science Fiction"}]','Released');
