--INSERT INTO movies VALUES(287,'tt0094812','Bull Durham','Veteran catcher Crash Davis is brought to the minor league Durham Bulls to help their up and coming pitching prospect, "Nuke" Laloosh. Their relationship gets off to a rocky start and is further complicated when baseball groupie Annie Savoy sets her sights on the two men.','[{"name": "Orion Pictures", "id": 41}, {"name": "The Mount Company", "id": 1312}]','1988-06-15',7000000,50888000,108.0000000000000071,NULL,'[{"id": 35, "name": "Comedy"}, {"id": 10749, "name": "Romance"}]','Released');
--INSERT INTO movies VALUES(288,'tt0044706','High Noon','High Noon is about a recently freed leader of a gang of bandits in the desert who is looking to get revenge on the Sheriff who put him in jail. A legendary western film from the Austrian director Fred Zinnemann.','[{"name": "United Artists", "id": 60}, {"name": "Stanley Kramer Productions", "id": 893}]','1952-03-27',730000,8000000,85.0,NULL,'[{"id": 37, "name": "Western"}]','Released');
